import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { withRouter } from 'react-router-dom';
import * as sessionActions from '../../actions/sessionActions';
import Input from '../input/Input';

class Login extends Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      user: {
        email: '',
        password: ''      
      }
    };

    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  getUserAccounts() {
    var accounts = [];
    fetch('https://reqres.in/api/users?page=2')
    .then(res => res.json())
    .then(data => {
      for (var i = 0; i < data.data.length; i++) {
        accounts.push({
          "id": data.data[i].id, 
          "last_name": data.data[i].last_name
        });
      }
    });
    // console.log(accounts);      // array-6
    // console.log(accounts.data); // undefined
    return accounts;
  }

  getAccounts = async() => {
    const response = await fetch('https://reqres.in/api/users?page=2');
    const json = await response.json();
    var accounts = [];
    for (var i = 0; i < json.data.length; i++) {
      accounts.push({
        "id": json.data[i].id, 
        "last_name": json.data[i].last_name
      });
    }
    // console.log(accounts);        // array-6
    // console.log(accounts.length); // 6
    return accounts;
  }

  // onSubmit = async(history) => {
  //   const { user } = this.state;

  //   const { login } = this.props.actions;
  //   var accounts = [];
  //   accounts = await this.getAccounts();

  //   for(var i = 0; i < accounts.length; i++) {
  //     if( user.email === accounts[i].last_name && user.password === accounts[i].id.toString()) {
  //       //{id: 7, last_name: "Lawson"}
  //       console.log("matched!!");
  //       login(user, history);
  //       return;
  //     }
  //   }

  //   alert("Login Failed");
  // }

  onSubmit(history) {
    const { user } = this.state;

    const { login } = this.props.actions;

    const targetUrl = 'http://localhost:8080/api/v1.0/auth/login';

    fetch(targetUrl, {
      method: "post",
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        email: user.email,
        password: user.password
      })
    })
    .then( (response) => { 
      // response.json().then(body => console.log(body));
      console.log(JSON.stringify(response.body));
      if(response.code === 0) {

        login(user, history);
        console.log("matched!!"); //vinhvd@miagi-so.com
      } else {
        alert("Login failed!!");
      }
    });
  }

  onChange(e) {
    const { value, name } = e.target;
    const { user } = this.state;
    user[name] = value;
    this.setState({ user });
  }

  render() {
    const { user: { email, password } } = this.state;
    const SubmitButton = withRouter(({ history }) => (
      <button
        onClick={() => this.onSubmit(history)}
        type="submit">Submit
      </button>
    ));

    return (
      <div>
        <h3>LOGIN</h3>
        <Input
          name="email"
          value="vinhvd@miagi-so.com"
          label="Email"
          type="email"
          onChange={this.onChange}
        />
        <Input
          name="password"
          value="123456123"
          label="Password"
          type="password"
          onChange={this.onChange}
        />
        <SubmitButton />
      </div>
    );
  }
}

const { object } = PropTypes;

Login.propTypes = {
  actions: object.isRequired
};

const mapDispatch = (dispatch) => {
  return {
    actions: bindActionCreators(sessionActions, dispatch)
  };
};

export default connect(null, mapDispatch)(Login);
