import { sessionService } from 'redux-react-session';
import * as sessionApi from '../api/sessionApi';

function checkValidParams(params) {
  if(params.email == ''){
    throw Error("Vui long nhap email!!")
  }
}

export const login = (param) => {
  return async () => {
      try {
        const {token, user} = await sessionApi.login(param); // sau await buoc phai la Promise, Promise co 2 trang thai pending va done, done thi co 2 TH resolve reject
        console.log(user);                                   // resolve thanh cong va tiep tuc luong, reject thi se di va catch
        await sessionService.saveSession(token);
        await sessionService.saveUser(user);
      } catch (ex) {
        throw ex; //reject
      }
  };
};

export const logout = (history) => {
  return () => {
    return sessionApi.logout().then(() => {
      sessionService.deleteSession();
      sessionService.deleteUser();
      history.push('/login');
    }).catch(err => {
      throw (err);
    });
  };
};
