import { sessionService } from 'redux-react-session';
import * as sessionApi from '../api/sessionApi';

export const login = (user, history) => {
  return () => {
    return sessionApi.login(user).then(response => { 
      console.log(response);
      //============response dat ten la gi cung duoc, nhung no la gia tri thanh cong cua promise 
      //sessionApi.login(user) la Promise
      const { token } = response.data;
      sessionService.saveSession({ token }) // saveSession tra ve Promise
      .then(() => {
        sessionService.saveUser(response.data.user)
        .then(() => {
          history.push('/');
        }).catch(err => console.error(err));
      }).catch(err => console.error(err));
    });
  };
};

export const logout = (history) => {
  return () => {
    return sessionApi.logout().then(() => {
      sessionService.deleteSession();
      sessionService.deleteUser();
      history.push('/login');
    }).catch(err => {
      throw (err);
    });
  };
};
