import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { withRouter } from 'react-router-dom';
import * as sessionActions from '../../actions/sessionActions';
import Input from '../input/Input';

class Login extends Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      user: {
        email: '',
        password: ''      
      }
    };

    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  getUserAccounts() {
    var accounts = [];
    fetch('https://reqres.in/api/users?page=2')
    .then(res => res.json())
    .then(data => {
      for (var i = 0; i < data.data.length; i++) {
        accounts.push({
          "id": data.data[i].id, 
          "last_name": data.data[i].last_name
        });
      }
    });

    return accounts;
  }

   getAccounts(callback) {
    var accounts = [];
    fetch('https://reqres.in/api/users?page=2')
        .then(res => res.json())
        .then(data => {
            for (var i = 0; i < data.data.length; i++) {
                accounts.push({
                    "id": data.data[i].id,
                    "last_name": data.data[i].last_name
                });
            }

            callback(accounts);
        });
  }



  onSubmit(history) {
    const { user } = this.state;
    //console.log(user);
    const { login } = this.props.actions;
    const accounts = this.getUserAccounts();

    console.log(accounts);
    console.log(accounts.length);
    console.log(accounts[0]);

    // console.log(user.email);
    // console.log(accounts[0].last_name);
    // console.log(user.email === accounts[0].last_name);
    // console.log(user.password);
    // console.log(accounts[0].id.toString());
    // console.log(user.password === accounts[0].id.toString());

    for(var i = 0; i < accounts.length; i++) {
      if( user.email === accounts[i].last_name && user.password === accounts[i].id.toString()) {
        //{id: 7, last_name: "Lawson"}
        console.log("matched!!");
        login(user, history);
        return;
      }
    }

    alert("Login Failed");
  }

  onChange(e) {
    const { value, name } = e.target;
    const { user } = this.state;
    user[name] = value;
    this.setState({ user });
  }

  render() {
    const { user: { email, password } } = this.state;
    const SubmitButton = withRouter(({ history }) => (
      <button
        onClick={() => this.onSubmit(history)}
        type="submit">Submit
      </button>
    ));

    return (
      <div>
        <h3>LOGIN</h3>
        <Input
          name="email"
          value={email}
          label="Email"
          type="email"
          onChange={this.onChange}
        />
        <Input
          name="password"
          value={password}
          label="Password"
          type="password"
          onChange={this.onChange}
        />
        <SubmitButton />
      </div>
    );
  }
}

const { object } = PropTypes;

Login.propTypes = {
  actions: object.isRequired
};

const mapDispatch = (dispatch) => {
  return {
    actions: bindActionCreators(sessionActions, dispatch)
  };
};

export default connect(null, mapDispatch)(Login);
